function chngCSvalue(){
    var e = document.getElementById('inistitue');
    document.getElementById('country_id').value = e.options[e.selectedIndex].getAttribute("country_id");
    document.getElementById('state_id').value =  e.options[e.selectedIndex].getAttribute("state");
}

function onlyFloat(str)
{
    var floatValue = parseFloat(str.value);
    var max_degree = document.getElementById('max_grade').value;

    if(isNaN(floatValue) == true)
    {
        alert('من فضلك ادخل المعدل التراكمي بشكل سليم');
        str.value = '0';
    }
    else
    {
        if(floatValue > max_degree){
            alert('درجة المعدل التراكمي يجب ان لا تكن اكبر من الدرجة العظمى');
            str.value = '0';
        }
        else{

        }
    }
}
