function onlyNumberKey(evt) {
    // Only ASCII character in that range allowed
    var ASCIICode = (evt.which) ? evt.which : evt.keyCode
    if (ASCIICode > 31 && (ASCIICode < 48 || ASCIICode > 57))
        return false;
    return true;
}
function checklength(){
    var value = document.getElementById('partner_mobile').value;
     if (value.length < 10 ) {
        alert('رقم الهاتف يجب ان يتكون من 10 ارقام');
       document.getElementById('partner_mobile').value ='';
     }
}
  function getExtension(filename)
{
    var parts = filename.value.split('.');
    var ext = parts[parts.length -1];
    if(ext != "pdf")
    {
       filename.value='';
       filename.style = 'border-color:red;'
       alert('غير مسموح باي ملفات ليست من نوع pdf');
    }
else{
filename.style = 'border-color:green;'
}
}
